$:/plugins/kookma/tamasha/styles/layout/.text:
/* buttons, slidenumber and progressbar are positioned based on
  .tamasha-presentation-frame
*/

.tamasha-viewer-nav {
	left: 1.5rem;
	position: absolute;
	bottom: 1rem;
	border-radius:2px;
	padding:4px 10px;
}

.tamasha-viewer-nav .tamasha-toolbar-separator {
	display:inline-block;
	width:2px;
	height:1.3em;
	margin:0 4px;
	vertical-align: top;
}

.tamasha-slide-content {
	font-size: 1.3rem;
	line-height: 1.85rem;
	padding: 5px 30px;
	margin: 0 auto;
	max-width: 900px;
	height:480px;
	overflow: hidden;
	margin-bottom: 4rem;
}

.tamasha-overlay .tamasha-slide-content {
	font-size:   clamp(1rem, 4vh, 2.5rem);
	line-height: clamp(1.5rem, 6vh, 4rem);
	height: auto;
	padding: 10px 8vh;
}

/* keep images inside slide content */
.tamasha-slide-content img{
	max-width:95%;
	max-height:95%;
}

.tamasha-slide-footer{	
	position: absolute; 
	left: 0; 
	right: 0; 
	margin-left: auto; 
	margin-right: auto; 
	bottom:1rem;
	max-width:100%;
	overflow:hidden;
	text-align:center;
	display:inline-block;
	max-width:40%;
}

.tamasha-overlay {
	height: 0; 
	position:fixed;
	top:0; left:0;
	z-index:1500;
	overflow-y: hidden;
	width:100%;
}

.tamasha-overlay.open {
	height: 100%;     /* use full screen height */
	max-height:100%;
}

.tamasha-progress{
	left:0;
	bottom: 0;
	position: absolute;
	width:100%;
	height:4px;
}

.tamasha-progress .bar{
	display: block;
	height:100%;
	width:100%;  
	transform-origin:0 0;
	transform:sclaeX(0);
	transition: transform 0.8s ease-in-out;
}

.tamasha-popup{
	left: 50px;
	position: absolute;
	bottom: 60px;

	padding-right:30px; /* to adapt with ol used inside popup */
	border-radius:4px;

	max-height:50%;
	overflow-y:auto;
	max-width:50%;
}

.tamasha-popup .tc-btn-invisible{
	text-align:left;
}

.tamasha-slide-number {
	position: absolute;
	bottom: 1rem;
	right: 1.5rem;
}

.tamasha-number-circle {
	box-sizing: content-box;  
	width: 1.5em;
	height: 1.5em;
	line-height: 1.5em;
	text-align: center;
	border-radius: 50%;    
}
/* change the circle size: increase line-height=width=height */

$:/plugins/kookma/tamasha/styles/overlay.text:
.tamasha-overlay {
	transition-property: all;
	transition-duration: 1s;
	transition-timing-function: cubic-bezier(0, 1, 0.5, 1);
	background-color:<<colour background>>;
}

$:/plugins/kookma/tamasha/styles/progres.text:
\define progress-light() #e5e5e5
\define progress-dark() #494949
\define bar-light() #9a9a9a
\define bar-dark() #d79921

<$vars 
light="Blanca Blue Muted ContrastLight DesertSand Rocker SolarFlare SolarizedLight SpartanDay Vanilla [[Notebook Beige]] [[Notebook Grey]]"
dark="ContrastDark CupertinoDark DarkPhotos GruvboxDark Nord SolarizedDark SpartanNight Twilight"
palette={{{ [[$:/palette]get[text]removeprefix[$:/palettes/]] }}}
>
<$list filter="[enlist<dark>search:title<palette>then[dark]else[light]]" variable=colorMacro>

.tamasha-progress{
	background-color:<$macrocall $name={{{[<colorMacro>addprefix[progress-]]}}} />;
}
.tamasha-progress .bar{
	background-color:<$macrocall $name={{{[<colorMacro>addprefix[bar-]]}}} />;
}
</$list>
</$vars>

$:/plugins/kookma/tamasha/styles/scrollb.text:
/* custom scrollbar */
.tamasha-popup::-webkit-scrollbar {
  width: 20px;
}

.tamasha-popup::-webkit-scrollbar-track {
  background-color: transparent;
}

.tamasha-popup::-webkit-scrollbar-thumb {
  background-color: #666666;
  border-radius: 20px;
  border: 6px solid transparent;
  background-clip: content-box;
}

.tamasha-popup::-webkit-scrollbar-thumb:hover {
  background-color: #909090; /* #a8bbbf;*/
}

$:/plugins/kookma/tamasha/styles/slideli.text:
.tamasha-popup{
	background-color: #323232;
	font-size:1em;
	box-shadow:0 4px 10px 0 rgba(0,0,0,0.2),0 4px 20px 0 rgba(0,0,0,0.19);
}

.tamasha-popup .tc-btn-invisible{
	color:#b9b9b9;
}

.tamasha-popup button:focus{
	outline:none;
}

/* change number color*/
.tamasha-popup li::marker { 
	color:#b9b9b9;
}

/* slide in view: current slide in the slideList */
.tamasha-popup .currentSlide .tc-btn-invisible,
.tamasha-popup .currentSlide::marker {
	color:#fff;
	font-size:0.9em;
}

$:/plugins/kookma/tamasha/styles/slidenu.text:
.tamasha-number-circle {
	border: 0.1em solid #666;
	font-size: 1.2em;
